import React from 'react';
import './Fields.scss';
import { Input, Radio , Checkbox, Form} from 'antd';
import "antd/dist/antd.css";

const FormItem = Form.Item;
const formItemLayout = {
    labelCol: {
      xs: { span: 24 },
      sm: { span: 6 }
    },
    wrapperCol: {
      xs: { span: 24 },
      sm: { span: 14 }
    }
  };

const makeField = Component => ({ input, meta, children, hasFeedback, label, ...rest }) => {
    const hasError = meta.touched && meta.invalid;
    return (
      <FormItem
        {...formItemLayout}
        label={label}
        validateStatus={hasError ? "error" : "success"}
        hasFeedback={hasFeedback && hasError}
        help={hasError && meta.error}
      >
        <Component {...input} {...rest} children={children} />
        <span className={(meta.touched && meta.error) ? 'warning' : 'hidden'}>{meta.error}</span>
      </FormItem>
    );
  };


export  const textField = makeField(Input);
export  const radioField = makeField(Radio);
export  const checkboxField = makeField(Checkbox);


