import React, { useState } from 'react';
import './Toggle.scss';

const Toggle = (props) => {
    const { item } = props;
    const [active1, setActive1] = useState(true);
    const [active2, setActive2] = useState(false);

    const handleClick = (name) => {
        if (name === true) {
            setActive1(true)
            setActive2(false)
        } else {
            setActive1(false)
            setActive2(true)
        }
    }
    return (
        <div  className='toggle__wrap'>
            <p> <span>{item.toggleName}</span> <span>:</span> </p>
            <div className='toggle'>
                <input type="text" className={active1 ? 'active' : undefined} name={item.name1.toString()} onClick={() => handleClick(item.name1)} defaultValue={item.value1} />
                <input typr="text" className={active2 ? 'active' : undefined} name={item.name2.toString()} onFocus={() => handleClick(item.name2)} defaultValue={item.value2} />
            </div>
        </div>
    )
}

export default Toggle;
