import React from 'react';
import './AddForm.scss';
import { Field, reduxForm } from 'redux-form'
import { textField } from '../../Auth/Fields';
import { validate } from './AddFormVallidate';
import username from '../../Assets/images/username.png';
import password from '../../Assets/images/password.png';
import Toggle from '../../Elements/Toggle/Toggle';

const AddForm = props => {
    const arr = [
        { toggleName: 'Active',  name1: true, name2: false, value1: 'Yes', value2: 'No' },
        { toggleName: 'Supervisor',  name1: true, name2: false, value1: 'Yes', value2: 'No' },
        { toggleName: 'Team Chat',  name1: true, name2: false, value1: 'Allow', value2: 'Disallow' }
    ]
    return (
        <form onSubmit={props.handleSubmit}>
            <Field
                name='name'
                type='text'
                component={textField}
                prefix={<img src={username} alt='password logo' />}
                placeholder='Name'
            />
            <Field
                name='email'
                type='email'
                component={textField}
                prefix={<img src={username} alt='password logo' />}
                placeholder='Email'
            />
            <Field
                name='password'
                type='password'
                component={textField}
                prefix={<img src={password} alt='password logo' />}
                placeholder='Password'
            />
            <Field
                name='password'
                type='password'
                component={textField}
                prefix={<img src={password} alt='password logo' />}
                placeholder='Change Password'
            />
            <div className='switch__list'>
                {
                    arr.map((item, i) => {
                        return (
                            <Toggle key={i} item={item} />
                        )
                    })
                }
            </div>
            <div className='channels_wrap'>
                <h3>Channels Allowed :</h3>
            </div>
            <button className='submit__button' type='submit'>Submit</button>
        </form>
    )
}

export default reduxForm({
    form: 'AddForm',
    validate,
})(AddForm)
