import React from 'react';
import './Fields.scss';
import { Input, Radio , Checkbox} from 'antd';
import "antd/dist/antd.css";


export const textField = ({
    input,
    type,
    prefix,
    placeholder,
    meta: { touched, error, }
}) => (
        <div className='input__wrapp'>
            <Input autoComplete={input.name} {...input} prefix={prefix} placeholder={placeholder} type={type} />
            {<span className={(touched && error) ? 'warning' : 'hidden'}>{error}</span>}
        </div>
    );


export const radioField = (input) => {
    return <div className='radio_wrapper'>
        <Radio {...input} id={input.name}>{input.label}</Radio>
    </div>
}


export const checkboxField = (input) => {
    return <div className='checkbox_wrap'>
        <Checkbox {...input} id={input.name}>{input.label}</Checkbox>
    </div>
}
