import React from 'react';
import './AddForm.scss';
import { Field, reduxForm } from 'redux-form'
import { textField, checkboxField, radioField } from '../../Auth/Fields';
import { validate } from './AddFormVallidate';
import username from '../../Assets/images/username.png';
import password from '../../Assets/images/password.png';
import email from '../../Assets/images/email.png';
import Toggle from '../../Elements/Toggle/Toggle';
import {Radio} from 'antd'


const AddForm = props => {
    const arr = [
        { toggleName: 'Active', name1: true, name2: false, value1: 'Yes', value2: 'No' },
        { toggleName: 'Supervisor', name1: true, name2: false, value1: 'Yes', value2: 'No' },
        { toggleName: 'Team Chat', name1: true, name2: false, value1: 'Allow', value2: 'Disallow' }
    ]
    return (
        <form onSubmit={props.handleSubmit}>
            <Field
                name='name'
                type='text'
                component={textField}
                prefix={<img src={username} alt='password logo' />}
                placeholder='Name'
            />
            <Field
                name='email'
                type='email'
                component={textField}
                prefix={<img src={email} alt='email logo' />}
                placeholder='Email'
            />
            <Field
                name='password'
                type='password'
                component={textField}
                prefix={<img src={password} alt='password logo' />}
                placeholder='Password'
            />
            <Field
                name='changePassword'
                type='password'
                component={textField}
                prefix={<img src={password} alt='password logo' />}
                placeholder='Change Password'
            />
            <div className='switch__list'>
                {
                    arr.map((item, i) => {
                        return (
                            <Toggle key={i} item={item} />
                        )
                    })
                }
            </div>
            <div className='channels_wrap'>
                <h3>Channels Allowed :</h3>
                <div className='chanel_list'>
                    <div>
                        <Field name="All" component={radioField} value="All">
                            <Radio value="All">All</Radio>
                        </Field>
                        <Field
                            name='Email'
                            component={checkboxField}
                            label='Email'
                        />

                    </div>
                    <div>
                        <Field
                            name='Webchat'
                            component={checkboxField}
                            label='Webchat'
                        />
                        <Field
                            name='SMS'
                            component={checkboxField}
                            label='SMS'
                        />
                    </div>
                    <div>
                        <Field
                            name='Facebook'
                            component={checkboxField}
                            label='Facebook'
                        />
                    </div>
                    <div>
                        <Field
                            name='Twitter'
                            component={checkboxField}
                            label='Twitter'
                        />
                    </div>
                </div>
            </div>
            <button className='submit__button' type='submit'>Submit</button>
        </form>
    )
}

export default reduxForm({
    form: 'AddForm',
    validate,
})(AddForm)
